mapper.update:
UPDATE USER_INFO


    PASSWORD = #{newPassword},


    NICKNAME = #{newNickName}


WHERE EMAIL = #{email}

update USER_INFO
set IMAGE = #{image}
where EMAIL = #{email}

mapper.select:
SELECT '자유' AS boardType, title, created_date, free_board_id AS boardId FROM free_board WHERE email = #{email}
UNION ALL
SELECT '제보' AS boardType, title, created_date, report_board_id AS boardId FROM report_board WHERE email = #{email}
ORDER BY created_date DESC

SELECT COUNT(*) FROM (
                       SELECT title FROM free_board WHERE email = #{email}
                       UNION ALL
                       SELECT title FROM report_board WHERE email = #{email}
                     ) AS total

SELECT COUNT(*) > 0
FROM USER_INFO
WHERE EMAIL = #{email}

SELECT COUNT(*) > 0
FROM USER_INFO
WHERE NICKNAME = #{nickName}

SELECT email, password, role, nickname, image
FROM user_info
WHERE email = #{email}
